export const setToken = (token) => ({
    type: 'SET_TOKEN',
    payload: token,
  });
  
  // Reducer
  const initialState = {
    token: null,
  };
  
  const tokenReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'SET_TOKEN':
        return {
          ...state,
          token: action.payload,
        };
      default:
        return state;
    }
  };
  
  export default tokenReducer;
