import { Avatar, Box, Button, Container, CssBaseline, Link, TextField, ThemeProvider, Typography, createTheme } from '@mui/material'
import React from 'react'
import {  useNavigate } from 'react-router-dom';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';

function Copyright(props) {
  return (
    <Typography variant="body2" color="text.secondary" align="center" {...props}>
      {'Copyright ©MTS '}
      <Link color="inherit" href="https://mui.com/">
        Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const defaultTheme = createTheme();

function ForgotPassword() {
  const [newpassword, setNewPassword] = React.useState('');
  const [error, setError] = React.useState('');
  const navigate = useNavigate();
 


  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData(event.currentTarget);
    
    const password = formData.get('newpassword');
    try {
      const myHeaders = new Headers();
      myHeaders.append('Content-Type', 'application/json');

      const requestOptions = {
        method: 'POST',
        headers: myHeaders,
        body: JSON.stringify({
        newpassword: newpassword,
        }),
        redirect: 'follow',
      };
      fetch(`http://localhost:8000/api/password/${token}`, requestOptions)
        .then(response => response.json())
        .then(result => {
          console.log(result)
          if (result.success) {   
              
            navigate('/')
          } else {
            alert('Error in Password reset')
          }
        })
        .catch(error => console.log('error', error));
    } catch (error) {

      console.error('Error during Setting:', error);

      setError('An error occurred. Please try again later.');
    }
  };
  return (
    <ThemeProvider theme={defaultTheme}>
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <Box
        sx={{
          marginTop: 8,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Reset Password
        </Typography>
        <Box component="form" onSubmit={handleSubmit} noValidate sx={{ mt: 1 }}>
          <TextField
            margin="normal"
            required
            fullWidth
            id="newPassword"
            label="Enter new Password"
            name="NewPassword"
            autoComplete="newPassword"
            autoFocus
            value={newpassword}
            onChange={(e) => setNewPassword(e.target.value)}

          />
          
          {error && (
            <Typography variant="body2" color="error" align="center" sx={{ mt: 1 }}>
              {error}
            </Typography>
          )}
          <Button
            type="submit"
            fullWidth
            variant="contained"
            sx={{ mt: 3, mb: 2 }}
          >
            Change Password
          </Button>
        
        </Box>
      </Box>
      <Copyright sx={{ mt: 8, mb: 4 }} />
    </Container>
  </ThemeProvider>
  )
}

export default ForgotPassword
